  const [inputText, setInputText] = useState("");
  const [todos, setTodos] = useState([]);

  return (
    <div className="App">

      <header>This is utsho List  </header>
      <Form inputText={inputText} todos={todos}  setTodos= {setTodos} setInputText ={setInputText} ></Form>
      <ToDoList></ToDoList>
      <Todo></Todo>
    </div>
  );  
}

export default App;
